import { Component, Input} from '@angular/core';

@Component({
  selector: 'app-user-info',
  templateUrl: './user-info.component.html',
  styleUrls: ['./user-info.component.scss'],
})
export class UserInfoComponent {
  @Input() name="";
  @Input() status="";
  @Input() avatar="";

  removeUser(){
    this.name = "";
    this.status = "";
    this.avatar = "";
  }
}
<div class="body">
    <h2></h2>
    <div class="user_info">
        <img [src]="avatar">
        <div class="user_info_text">
            <h2>{{name}}</h2>
            <p>{{status}}</p>
        </div>
    </div>
    <div class="button">
        <button type="button"
        (click)="removeUser()"
        >Remove user</button>
    </div>
</div>
